mapper.select:
SELECT COUNT(*)
FROM LECTURE
WHERE LECTURE_NO = #{lectureNo, jdbcType=NUMERIC}

mapper.update:
UPDATE LECTURE
   SET TITLE        = #{title, jdbcType=VARCHAR},
       LECTURE_DESC = #{lectureDesc, jdbcType=CLOB},
       VISIBILITY   = #{visibility, jdbcType=NUMERIC}
 WHERE LECTURE_NO  = #{lectureNo, jdbcType=NUMERIC}
